function A();  r1 = ['00';'01';'10';'11'];  r2 = ['0000'; '0101'; '1010'; '1111'; '0011'; '0110'; '1001'; '1100'];  r3 = [        '00000000'; '00001111';        '01010101'; '01011010';        '10101010'; '10100101';        '11111111'; '11110000';        '00110011'; '00111100';        '01100110'; '01101001';        '10011001'; '10010110';        '11001100'; '11000011'       ];  for i = 1:size(r1, 1)    printf("%s %d\n", r1(i,:), wt(r1(i,:)));  endfor  printf("\n");    for i = 1:size(r2, 1)    printf("%s %d\n", r2(i,:), wt(r2(i,:)));  endfor  printf("\n");    for i = 1:size(r3, 1)    printf("%s %d\n", r3(i,:), wt(r3(i,:)));  endfor    printf("\n");  
endfunctionfunction w = wt(s);  w=size(strchr(s,'1'),2);endfunction